import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  TextField,
  Typography,
  Box,
  Alert,
  CircularProgress,
} from '@mui/material';
import { getPrints, searchPrints } from '../services/api';
import PrintCard from '../components/PrintCard';

const PrintsPage = () => {
  // Handler for editing a print
  const handleEdit = (print) => {
    // TODO: Replace with actual edit dialog logic
    alert(`Edit print: ${print.name}`);
  };

  // Handler for deleting a print
  const handleDelete = (printId) => {
    // TODO: Replace with actual delete logic
    alert(`Delete print with id: ${printId}`);
  };

  const [prints, setPrints] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [searchTimeout, setSearchTimeout] = useState(null);

  const loadPrints = async (query = '') => {
    try {
      setLoading(true);
      const response = await (query ? searchPrints(query) : getPrints());
      setPrints(response.data);
    } catch (err) {
      setError('Failed to load prints');
      console.error('Error loading prints:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadPrints();
  }, []);

  const handleSearch = (e) => {
    const query = e.target.value;
    setSearchQuery(query);

    // Debounce search
    if (searchTimeout) {
      clearTimeout(searchTimeout);
    }

    setSearchTimeout(
      setTimeout(() => {
        loadPrints(query);
      }, 500)
    );
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Container>
      <Box sx={{ mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Available Prints
        </Typography>

        <TextField
          label="Search Prints"
          variant="outlined"
          fullWidth
          value={searchQuery}
          onChange={handleSearch}
          sx={{ mb: 2 }}
        />

        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}
      </Box>



      <Grid container spacing={3}>
        <Grid item xs={12}><div>---START PRINTS MAPPING---</div></Grid>
        {Array.isArray(prints) && prints.length > 0
          ? prints.map((print, idx) => {
  if (typeof print !== 'object' || print === null || !print.id) {
    console.log('NON-OBJECT IN PRINTS:', print, idx);
    return null;
  }
  return (
    <Grid item xs={12} sm={6} md={4} key={print.id || idx}>
      <PrintCard print={print} onEdit={handleEdit} onDelete={handleDelete} />
    </Grid>
  );
})
          : (
            <Grid item xs={12}>
              <Alert severity="info">
                No prints found{searchQuery ? ' matching your search' : ''}.
              </Alert>
            </Grid>
          )}
        <Grid item xs={12}><div>---END PRINTS MAPPING---</div></Grid>
      </Grid>
    </Container>
  );
};

export default PrintsPage;
